// default render page
$(document).ready(function() {
    var data = {member_id:"",gender:"", member_name: "", start_date: "", end_date: ""};
    generate_members_info(data);
});

// render date datewise
$(document).on('click','#filter_member', function(){   
    var member_id = $('input#member_id').val();
    var gender = $('select#filter-gender').val(); 
    var member_name = $('input#member_name').val(); 
    var startDate = $('input#start_date').val();    
    var endDate = $('input#end_date').val();
    var data = {member_id:member_id,gender:gender, member_name:member_name,startDate:startDate, endDate:endDate};
    generate_members_info(data);
});
// generate Element Table
function generate_members_info(element){	
    var start_date = $('input#start-date').val(); 
    var end_date = $('input#end-date').val();
    var date_range;
    if (start_date !== '' && end_date !== '') {
        date_range = 'From '+start_date+' To '+end_date;
    }else{
        date_range = 'Printed On '+currentDate; 
    }
    $.ajax({
        url: baseURL + 'admin/reports/generate_members_information',
        data: {'member_id' : element.member_id,'gender' : element.gender,
            'member_name' : element.member_name,
            'start_date' : element.startDate , 'end_date' : element.endDate},
        type: 'post', 
        dataType: 'json',
        beforeSend: function () {
            $('#generate_members').html('<div class="text-center">'+
                '<i class="fa fa-spinner fa-pulse fa-4x fa-fw"></i></div>');
        },			 
        success: function (html) {
            var dataTable='<table id="members_table" class="table table-striped"'+
            'cellspacing="0" width="100%"></table>';
            $('#generate_members').html(dataTable);		
            var table = $('#members_table').DataTable({
                data: html.data,
                "bPaginate": true,
                "bLengthChange": true,
                "pageLength" : 25,
                "lengthMenu" : [[10,25,50,100],[10,25,50,100]],
                "bFilter": false,
                "bInfo": true,
                "bAutoWidth": true,
                columns: [
                { title: "S.No", "width": "2%"},
                { title: "Member Name", "width": "17%"},
                { title: "Account No", "width": "10%"},
                { title: "Gender", "width": "5%"},
                { title: "Account Balance", "width": "12%"},
                { title: "Phone", "width": "2%"},
                { title: "Address", "width": "12%"},				
                { title: "Joined Date", "width": "12%"}
                ],
                dom: 'lBfrtip',
                buttons: [
                //'copy','csv', 'excel', 'pdf', 'print'
                {
                    extend: 'excel',
                    className: 'btn btn-secondary',
                    titleAttr: 'Excel Export',
                    text: '<i class="fa fa-file-excel-o"></i>',
                    filename: 'Registered Members Information',
                    title: 'Registered Members Information',
                    extension: '.xlsx'
                },
                {
                    extend: 'csv',
                    className: 'btn  btn-primary',
                    titleAttr: 'CSV Export',
                    text: '<i class="fa fa-bars"></i>',
                    filename: 'Registered Members Information',
                    title: 'Registered Members Information',
                    extension: '.csv'
                },
                {
                    extend: 'print',
                    customize: function (win) {
                        $(win.document.body)
                        .css('font-size', '10pt')
                        .prepend(
                        '<img src='+systemLogo+' style="position:absolute; top:0;'+
                        ' left:0;height:90px" />'
                        );
                        $(win.document.body).find('table')
                        .addClass('compact')
                        .css('font-size', 'inherit');
                    },
                    className: 'btn btn-secondary',
                    titleAttr: 'Print Export',
                    text: '<i class="fa fa-print"></i>',
                    title: '<h3>'+systemName+'</h3>'+
                    '<h4 class="text-center">Registered Members Information</h4>'+
                    '<h5 class="text-center">'+ date_range+'</h5>',
                },                
                ],
            });
        }		 
    });
}

// get DatePicker
$(document).on('focus', '.getDatePicker', function () {
    $(this).datepicker({
        changeMonth: true,
        changeYear: true,
        dateFormat: "yy-mm-dd",
        yearRange: "2012:2050"
    });
});
